import React from 'react';
import './Login.css';
import {TextField } from '@material-ui/core';
import $ from 'jquery';


class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        isVisible: true
      }
      // Bindings
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleRemount = this.handleRemount.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();
    let data = $('form[name="login"]').jsonify({stringify: true});
    console.log(data);
    $.ajax({
        type: "POST",
        url: "https://myfirstwebsite-159214.appspot.com/rest/login",
        contentType: "application/json; charset=utf-8",
        crossDomain: true,
        //dataType: "json",
        success: function(response) {
            if(response) {
                alert("Got token with id: " + response.tokenID);
                // Store token id for later use in localStorage
                localStorage.setItem('tokenID', response.tokenID);
            }
            else {
                alert("No response");
            }
        },
        error: function(response) {
            alert("Error: "+ response.status);
        },
        data: data
    });

    this.setState({
      isVisible: false
    }, function() {
      console.log(this.state.isVisible);
    });
    return false;
  }
  handleRemount(e) {
    this.setState({
      isVisible: true
    }, function() {
      console.log(this.state.isVisible)
    });
    e.preventDefault();
  }
  render() {

    // const for React CSS transition declaration
    let component = this.state.isVisible ? <Login2 onSubmit={ this.handleSubmit } key='Login2'/> : <Login3 onClick={ this.handleRemount } key='Login3'/>;

      return <div>{ component }</div>
  }
}

















