import React from 'react';
import './Login.css';
import { Button, TextField } from '@material-ui/core';

class Login extends React.Component{

  constructor(props) {
    super(props);
    this.state={
       warning   : '',
       username  : '',
       password  : '',
       warning2  : ''
    }
    this.handleChange = this.handleChange.bind(this);
  }

   handleChange(event) 
   {

         const target = event.target;
         const value = target.name === 'isGoing' ? target.checked : target.value;
         const name = target.name;
         this.setState({
           [name]: value    });

         if(this.state.username.length<2 || this.state.username.length>8)
             {

               this.setState({warning: "higher 2 and lower 9"})
             }else{
               this.setState({warning: ""})
             };

         var re = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*(\W|_)).{0,10}$/;

         if(!re.test(this.state.password))
             {
               this.setState({warning2: "have a num, lower,upper,a special"})

             }
             else{
               this.setState({warning2: ""})
             }


   }
   handleSubmit(event) {
     event.preventDefault();
   }

  render(){
  return (
    <div>
        <header>
          <div className="container">

          </div>
        </header>
        <main>
          <div className="container">
            <div onSubmit={this.handleSubmit} className="login-form">
              <form action method="post">
                <h1>Đăng nhập vào website</h1>
                <div className="input-box">
                  <i />
                  <TextField className="input-box input" type="text" placeholder="Nhập username" value={this.state.username} type='username' name='username' onChange={this.handleChange} placeholder='username' helperText={this.state.warning}/>
                </div>
                <div className="input-box">
                  <i />
                  <TextField className="input-box input" type="password" placeholder="Nhập mật khẩu" value={this.state.password} type='password' name='password' onChange={this.handleChange} placeholder='password' helperText={this.state.warning2} />
                </div>
                <div className="btn-box">
                  <button type="submit">
                    Đăng nhập
                  </button>
                </div>
              </form>
            </div>
          </div>
        </main>
        <footer>
          <div className="container">

          </div>
        </footer>
      </div>
  );
}
}

export default Login;
